import React from 'react';
import Item from './Item';
import './css/List.css';
import {useState, useEffect} from "react";
import Axios  from "axios";
import Footer from './footer';
import { useHistory } from "react-router-dom";
import Folder from './folders';
import './css/List_Movement.css';
import Menu from './Menu';
import {useUsuario} from './context/usuario-context';

const userAuthenticated = () =>{
  
  
  Axios.get('isUserAuth',{
    headers: {
      "Authorization" : localStorage.getItem('token')
    }

  }).then((response)=>{
     
   console.log("hola",response.data);
 })
}

  

  



  

function Budget(props){
  const {username, password, id, setUsername, setPassword, setid} = useUsuario();
  const [datafolder, setdatafolder] = useState([])
  
  const [user, setUser] = useState(localStorage.getItem('user'))
 
    
  let history = useHistory();
 
  // useEffect(() => {
  //   userAuthenticated()
  
  // },[]);
    
  const Logout = () =>{
    localStorage.removeItem('token')
    history.push("/register")

  }


  useEffect( () => {

    async function folder () {
      setid(localStorage.getItem('user'))
      console.log("second",user)
      await Axios.get('folders/'+ id,{
      }).then((response) => {
        console.log(response.data.result) 
        setdatafolder(response.data.result)
        
      });
    }
    
    folder()
  },[id]);  


  // function addnewfolder(){
  //   await Axios.get('folders/'+ id,{
  //   }).then((response) => {
  //     console.log(response.data.result) 
  //     setdatafolder(response.data.result)
      
  //   });
  // }

  // }
  
    
    return(
      <div>
        <div className="new-item-panel-container">
            <div className="new-item-panel">
                <form >
                    <p>
                    <label>Título del libro</label><br />
                    <input type="text" name="title" className="input" onChange={this.onChangeTitle} />
                    </p>

                    <p>
                    <label>Nombre de imagen</label><br />
                    <input type="text" name="image" className="input" onChange={this.onChangeImage} />
                    </p>

                    <p>
                    <label>Calificación</label><br />
                    <select onChange={this.onChangeRating}>
                        <option value="1">1</option>
                        <option value="2">2</option>
                        <option value="3">3</option>
                        <option value="4">4</option>
                        <option value="5">5</option>
                    </select>
                    </p>
                    <input type="submit" className="button btn-blue" value="Registrar libro" />
                    <button className="button btn-normal" onClick={this.props.onhide}>Cancelar</button>
                </form>
            </div>
        </div>
    
      
          <div>
            <Menu/>
          </div>
          <div className="box-movement">

              {/* <h1>Welcome {props.items}</h1> */}
              {/* <div> 
              <button style={{background : 'red'}} onClick={userAuthenticated}>Check if Authenticated</button> 
              <button style={{background : 'blue'}} onClick={Logout}>Check if Authenticated</button>
              </div>    */}
            
                <div className="list-movement">
                  {datafolder.map((movement)=>{
                    return (
                      <Folder key={movement.folder_id} user={movement.folder_id} type={movement.name}/>
                    )
                  })}
                </div>
                
            
          </div>
      </div>              
    );
}


export default Budget;
