import React from 'react'
import "./css/task.css"
import { useState } from 'react'


const Task = (props) => {
    const [done, setdone] = useState(true)
    const check = () =>{
        if(props.done === 1){
            setdone(true)
        }
    }
   
    return (
        <div className='task-box'>
            <input className="check" defaultChecked={done} type="checkbox"></input>
            <h1 className='description'>{props.description}</h1>
        </div>
    )
}

export default Task;
