    }
   

    return (
        
        <div className='movement'>
            
            <h1 onClick={updatefolderid} className='name'>{props.name}</h1>
            <img onClick={()=>deleteFolder(props.id)} className="trash-img" src="../img/trash.png" alt="delete-img" />
        
        </div>          
        
        
    )
}

export default Folder;
