import React from 'react';
import Item from './Item';
import './css/List.css';
import {useState, useEffect} from "react";
import Axios  from "axios";
import Footer from './footer';
import { useHistory } from "react-router-dom";
import Folder from './folders';
import './css/List_Movement.css';
import Menu from './Menu';
import {useUsuario} from './context/usuario-context';

// const userAuthenticated = () =>{
  
  
//   Axios.get('isUserAuth',{
//     headers: {
//       "Authorization" : localStorage.getItem('token')
//     }

//   }).then((response)=>{
     
//    console.log("hola",response.data);
//  })
// }

  
function FolderPanel(props){
  console.log("budget")
  const {id,folderscount,setid} = useUsuario();
  const [datafolder, setdatafolder] = useState([])
  const [newfolder, setnewfolder] = useState('')
  
  let history = useHistory();
 
    
  const Logout = () =>{
    localStorage.removeItem('token')
    history.push("/register")
  }



  const AddItemForm = (props) => (
    
    <div>    
        <div>
          <Menu/>
        </div>
        <div className="box-movement">

              <p className="text-newfolder">Create a new folder</p>
              <div className="addfolder-container">
              <form onSubmit={props.addnewFolder}>
                <input className="newfolder"type="text" autoComplete="off" value={newfolder} placeholder="NEW FOLDER"
                // onChange={(e)=>{
                //   setnewfolder(e.target.value);
                // }}
                >
                </input>
                </form>  
                <button className="button-folder" 
                onClick={()=> {setnewfolder("");addnewFolder(addnewFolder) }}
                >Add</button>
              </div>
          </div>
        </div>
             
          
      )

  

  useEffect( () => {

    const folder = () => {
      
      setid(localStorage.getItem('user'))
      Axios.get('folders/'+ id,{
      }).then((response) => {
        console.log(response.data.result) 
        setdatafolder(response.data.result)
        
      });
    }
    
    folder()
  },[id]);  


  const addnewFolder = (event) => {
    event.preventDefault();
    Axios.post('save_folder',{
      name : newfolder,
      user_id : id
    })
    setdatafolder((prevItems)=>[...prevItems,{name:newfolder, user_id: id}]); 

  return (
    <div>
      <AddItemForm addnewFolder={addnewFolder} />
     
    </div>
  );
  }


  

                {/* <div className="list-movement">
                
                  {datafolder.map((movement)=>{
                    
                    return (
                      
                      <Folder  key={movement.folder_id} id={movement.folder_id} name={movement.name}/>
                    )
                  })}
                </div>
          </div>
      </div>              
    ); */}
}






export default  FolderPanel;
